INSERT INTO dormitories (id, category_id, name, comment, main_description, sub_description, city_id, district_id, is_like, view_count)
VALUES 
(1, 4, '제주조랑말', '감귤과수원의 풍경을 끌어들인 특별한 공간에서의 휴식', '제주조랑말은 감귤과수원 안에 있는 비정형적인 외관으로 프라이빗한 휴식 그리고 충실한 편안함과 동시에 특별한 제주에서의 휴식을 제공하고 싶습니다.', '제주조랑말에서 아침에 눈을 뜨면 누운채로도 푸릇푸릇 반짝이는 귤밭을 시야에 채울 수 있으며 방문을 열면 귤 밭 사이사이에서 조잘거리는 새소리가 들립니다. 천연덕스럽게 야외복도를 넘어 자라고 있는 로즈마리의 향도 차분하게 온몸으로 스며듭니다. 느지막히 일어나서 바로 내린 커피향과 갓구운 빵냄새가 가득한 카페에서 감귤밭에 둘러쌓여 조식을 즐겨 보세요. 제주토끼는 앞만보고 달리기를 강요받는 도시사람들에게 잠시 달리기를 멈추고 몸과 마음이 쉴 수 있는 휴식을 선물하고 싶습니다.', 1, 1, 0, 100),
(2, 2, '스테이에프', '일상으로부터 벗어나 바다를 마주보다', '스테이에프는 동해바다와 제가 사랑하는 사람의 이니셜 입니다. 바닷가에서 빛나던 그 사람은 저에게 또 다른 바다였습니다. 평온함을 찾아 바다로 떠났던 그때의 그 느낌이 당신에게도 전달되기를 바랍니다.', '동틀 무렵 침대에 누워 통창으로 보이는 해를 바라보며 시작하여, 해질 무렵 유리블럭으로 들어오는 노을빛은 스테이디의 또 다른 매력입니다. 숙소 바로 앞 해변은 조용하고 한적한 장소입니다. 연인 또는 가족간의 오붓한 피크닉을 떠나 보세요. 해질 무렵 루프탑에서는 노을을 즐길 수 있고, 저녁엔 침대에 누워 빔프로젝터로 영화를 볼 수 있습니다.', 2, 2, 0, 200),
(3, 4, '라온다', '허기진 마음을 가득 채워가는 곳', '‘라온다’는 강릉 교동 조용한 주택가에 자리한 언덕 위 이층집입니다. 90년대에 지어진 옛 주택을 리모델링하여 만들어진 라온가는 강릉을 찾는 여행자들에게 1층 전체를 내어주고 있습니다. ‘라온’은 순우리말로 ‘즐거운’이라는 뜻을 가지고 있습니다. 뜻도 이름도 예쁜 라온가에서 즐거운 시간을 보내고, 앞으로도 즐거움이 함께하는 나날을 보내라는 의미가 담겨있습니다.', '라온다 내에 준비된 책과 음악, 은은한 조도의 조명과 함께 복잡한 생각들을 잠시 내려놓고 사색과 휴식을 취할 수 있습니다. 또 라온가 뒤의 이름 없는 조그마한 산에는 삼림욕을 할 수 있는 길이 조성되어 있어 간단히 산책하기에도 좋습니다. 바쁜 일상생활 속에서 느끼는 허기진 마음을 이곳 라온다에서 든든히 채워 치유와 회복의 시간을 가져보길 바랍니다.', 2, 3, 0, 150),
(4, 4, '칠월학교', '자연이 흐르는 가족의 안식처', '낭만의 도시 춘천의 끝자락, 옛 지암 국교 분교장 자리로 손닿지 않은 자연의 아름다움이 남아있는 조용한 곳입니다. 1969년 개교해 15년 동안 아이들의 배움터이자 놀이터였던 이곳은 여타 다른 분교와 마찬가지로 학생 수가 점점 줄어들어 1982년 결국 폐교되었습니다.', '그 후 오랜 시간 자연 속에서 방치되었던 곳으로 그대로 방치되어 있기에는 너무나 아름다운 이 공간에 비플러스엠만의 색을 입히고 새로운 숨결을 불어넣어 재탄생 시켰습니다. 순박한 아이들의 이야기와 자연이 주는 아름다움을 그대로 안고 모든 이들이 편히 쉬었다 갈 수 있는 곳으로 ‘칠월학교’를 방문하는 모든 이들이 자연이 주는 아름다움 속에서 작은 것의 소중함을 느낄 수 있길 바랍니다.', 2, 4, 0, 50),
(5, 3, '우연 스테이', '산과 계곡, 자연과 함께하는 온전한 휴식', '우연 스테이는 전북 완주군 소양면 오성 한옥 마을 내에 위치한 풀빌라입니다. 자연 친화적인 180평의 넓은 공간을 단독으로 사용할 수 있습니다. 주변은 한옥이 어우러져 있으며 스테이의 앞과 뒤에는 산이, 옆으로는 계곡이 흘러 객실 내에서도 항상 계곡 흐르는 소리가 잔잔하게 들려 명상이나 사색에도 좋은 공간 우연스테이 입니다.', '20년 전에 지은 황토 주택을 리모델링하여 탄생한 우연스테이는 우드와 화이트 톤으로 정갈하면서도 편안함을 추구합니다. 외부는 이국적이지만 내부는 한국적인 아름다움과 멋을 느낄 수 있으며, 건물 뒤쪽으로는 게스트만이 사용 가능한 원두막과 아이들이 뛰어 놀 수 있는 잔디정원이 있습니다. 시시각각 변하는 계절의 특색에 맞게 창문으로 보여지는 자연 경관이 하나의 갤러리를 연상시킵니다.', 3, 5, 0, 25),
(6, 1, '예스모어', '모든 것을 내려 놓으며, 비로소 느낄 수 있는 머무름의 시간', 'Something is more', '우리는 삶을 살아가며 수없이 많은 외부와의 관계를 만들어갑니다. 하지만 이러한 것들은 때때로 우리의 일상을 희미하고 무감각하게 하며, 명확하게 잡히지 않는 무엇인가를 끊임없이 추구하게 합니다. 우리를 둘러싸고 있는 모든 것들을 내려 놓는 순간, 미묘한 삶의 감각과 머무름의 가치가 드러납니다.', 4, 6, 0, 75);